mapper.select:
SELECT memId
FROM member
WHERE memId=#{memId}

mapper.update:
UPDATE member
SET memParentNo=#{memParentNo}, signUpCheck=1
WHERE memId=#{memId}

mapper.select:
SELECT memPwd
FROM member
WHERE memId=#{id}

